import React, { Component } from 'react';

//Route Componentini projemize dahil ediyoruz ve bu sayede Link kullanarak Componentleri arayüzde oluşturmuş oluyoruz. 
import { BrowserRouter as Router, Route, Link, NavLink } from 'react-router-dom';
import SignUpForm from './pages/SignUpForm';
import SignInForm from './pages/SignInForm';
import './App.css';

class App extends Component {
  render() { 
  return (
    <Router>
    <div className="App">
    <div className="App__Aside"></div>
    <div className="App__Form">
      <div className="PageSwitcher">
        <a href="#" className="PageSwitcher__Item">Sign In</a>
        <a href="#" className="PageSwitcher__Item PageSwitcher__Item--Active">Sign Up</a>
      </div>

      <div className="FormTitle">
        <NavLink to ="/sign-in" activeClassName="FormTitle__Link--Active" className="FormTitle__Link">Sign In</NavLink> or <Link exact to="/" activeClassName="FormTitle__Link FormTitle__Link--Active" className="FormTitle__Link">Sign Up</Link>
      </div>

      <Route exact path="/" component={SignUpForm}>

      </Route>
      <Route path="/sign-in" component={SignInForm}>

      </Route>
    </div>
    </div>
    </Router>
  );
}
}

export default App;
